// 表示可以向Square组件传递一个名为value的props
function Square({ value }) {

  function handleClick() {
    console.log('cliked!');
  }


  // 需要从组件中渲染名为value的JS变量 而不是“value”这个词
  // 要从JSX“转义到JS” 需要使用大括号
  return <button className="square">{value}</button>;

}

export default function Board() {
  return(
    // React组件必须返回单个JSX元素 如果要返回多个
    // 可以用<></>包裹多个相邻的JSX元素
    <>
      <div className="board-row">
        <Square value="1" />
        <Square value="2" />
        <Square value="3" />
      </div>
      <div className="board-row">
        <Square value="4" />
        <Square value="5" />
        <Square value="6" />
      </div>
      <div className="board-row">
        <Square value="7" />
        <Square value="8" />
        <Square value="9"/>
      </div>
    </>
  )
}
