  // 需要从组件中渲染名为value的JS变量 而不是“value”这个词
  // 要从JSX“转义到JS” 需要使用大括号
  return(
    <button
      className="square"
      onClick={onSquareClick}
    >
        {value}
    </button>
  );
}

