import {useState} from 'react';

// 表示可以向Square组件传递一个名为value的props
function Square() {
  const [value, setValue] = useState(null);

  function handleClick() {
    setValue('X');
  }
  // 需要从组件中渲染名为value的JS变量 而不是“value”这个词
  // 要从JSX“转义到JS” 需要使用大括号
  return(
    <button
      className="square"
      onClick={handleClick}
    >
        {value}
    </button>
  );
}

export default function Board() {
  const [squares, setSquares] = useState(Array)
  return(
    // React组件必须返回单个JSX元素 如果要返回多个
    // 可以用<></>包裹多个相邻的JSX元素
    <>
      <div className="board-row">
        <Square />
        <Square />
        <Square />
      </div>
      <div className="board-row">
        <Square />
        <Square />
        <Square />
      </div>
      <div className="board-row">
        <Square />
        <Square />
        <Square />
      </div>
    </>
  )
}
